#include <random>
#include <iostream>
#include <vector>

template <typename T>
std::vector<int> sort(T array) {
    
    std::random_device dev;
    std::mt19937 rng(dev());
    std::uniform_int_distribution<std::mt19937::result_type> dist(1,10);
    
    std::vector<int> newArray (1000000000);
    
    for (int i = 0; i < newArray.size(); i++) {
        for (int j = 0; j < newArray.size(); j++) {
            for (int k = 0; k < newArray.size(); k++) {
                for (int l = 0; l < newArray.size(); l++) {
                    for (int m = 0; m < newArray.size(); m++) {
                        for (int n = 0; n < newArray.size(); n++) {
                            for (int o = 0; o < newArray.size(); o++) {
                                for (int p = 0; p < newArray.size(); p++) {
                                    for (int q = 0; q < newArray.size(); q++) {
                                        for (int r = 0; r < newArray.size(); r++) {
                                            newArray[i] = dist(rng);
                                            std::cout << "Hi! We're \"sorting\" your array. Please come back during the heat death of the universe to recieve your sorted array!\n";
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
    
    
    return newArray;
}

